                i, index, line = '';
            for (i = 0; i < array.length; i++) {
                line = '';
                for (index = 0; index < cellName.length; index++) {
                    line += array[i][cellName[index].name] + sepCharacter;
                }
                str += line + '\r\n';
            }
            str = colNames.toString().replace(/,/g, sepCharacter) + '\r\n' + str;
